mapper.select:
select

from audits a
where
  a.created_at >= #{start} and a.created_at < #{end}
limit #{pagination.pageSize,jdbcType=INTEGER} offset #{pagination.offset,jdbcType=INTEGER}

select  from (
  select
    row_number() over(order by created_at, uuid) as row_number,

    from audits
    where created_at >= #{start} and created_at < #{end}
) as a
where
a.row_number between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}
order by a.row_number asc

select  from (
  select rownum as rn, t.* from (
    select

    from audits
    where created_at >= #{start} and created_at < #{end}
  ) t
) a
where
  a.rn between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}
order by a.rn asc

mapper.delete:
delete from audits
where uuid in (select a.uuid from audits a where a.created_at < #{threshold,jdbcType=BIGINT}
order by a.created_at limit 100000)

delete from audits
where uuid in (select top 100000 a.uuid from audits a where a.created_at < #{threshold,jdbcType=BIGINT}
  order by a.created_at)

delete from audits
where uuid in (select a.uuid from audits a where a.created_at < #{threshold,jdbcType=BIGINT}
  order by a.created_at fetch first 100000 rows only)
