mapper.select:
select


order by ca.id desc
limit #{pagination.pageSize,jdbcType=INTEGER} offset #{pagination.offset,jdbcType=INTEGER}

select * from (
select row_number() over(order by id desc) as number,


  ) as query
where
  query.number between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}
order by id desc

select * from (
  select rownum as rn, t.* from (
    select


    order by ca.id desc
  ) t
) t
where
  t.rn between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}

mapper.sql:
from ce_activity ca
left outer join ce_scanner_context csc on csc.task_uuid = ca.uuid


    and ca.is_last=${_true}


    and ca.component_uuid in

      #{cUuid,jdbcType=VARCHAR}



    and ca.status in

      #{status,jdbcType=VARCHAR}



    and ca.task_type=#{query.type,jdbcType=VARCHAR}


    and ca.submitted_at >= #{query.minSubmittedAt,jdbcType=BIGINT}


    and ca.executed_at <= #{query.maxExecutedAt,jdbcType=BIGINT}

mapper.select:
select
  count(1)
from
  ce_activity
where
  status=#{status,jdbcType=VARCHAR}
  and is_last=${_true}

    and component_uuid=#{componentUuid,jdbcType=VARCHAR}

mapper.update:
update ce_activity
set is_last=${_false},
updated_at=#{updatedAt,jdbcType=BIGINT}
where is_last=${_true} and is_last_key=#{isLastKey,jdbcType=VARCHAR}
