mapper.update:
update project_branches
set
  kee = #{newBranchName, jdbcType=VARCHAR},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{branchUuid, jdbcType=VARCHAR}

update project_branches
set
  exclude_from_purge = #{excludeFromPurge, jdbcType=BOOLEAN},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{uuid, jdbcType=VARCHAR}

mapper.select:
select distinct pb.project_uuid
from project_branches pb
where pb.need_issue_sync = ${_true}
and pb.project_uuid in

  #{uuid,jdbcType=VARCHAR}

select count(pb.uuid)
from project_branches pb
where
pb.branch_type = #{branchType, jdbcType=VARCHAR}
and pb.created_at >= #{sinceDate, jdbcType=BIGINT}

mapper.update:
update project_branches
set
  need_issue_sync = ${_true},
  updated_at = #{now, jdbcType=BIGINT}
where
  project_uuid = #{projectUuid, jdbcType=VARCHAR}

update project_branches
set
  need_issue_sync = #{needIssueSync, jdbcType=BOOLEAN},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{uuid, jdbcType=VARCHAR}

mapper.sql:
select case when exists ( select pb.project_uuid, pb.need_issue_sync from project_branches pb join components c on pb.project_uuid = c.branch_uuid where c.kee in
#{componentKey,jdbcType=VARCHAR}
and pb.need_issue_sync = ${_true} ) then 1 else 0 end

mapper.update:
update project_branches
set
measures_migrated = #{measuresMigrated, jdbcType=BOOLEAN},
updated_at = #{now, jdbcType=BIGINT}
where
uuid = #{uuid, jdbcType=VARCHAR}
