mapper.select:
SELECT

FROM components p
LEFT JOIN project_branches pb on pb.uuid = p.branch_uuid
WHERE
p.enabled=${_true}
AND p.kee IN

  #{key,jdbcType=VARCHAR}



    AND pb.kee=#{branch,jdbcType=VARCHAR}
    AND pb.branch_type = 'BRANCH'


    AND pb.kee=#{pullRequest,jdbcType=VARCHAR}
    AND pb.branch_type = 'PULL_REQUEST'


    AND

select
from components p where p.uuid in
#{uuid,jdbcType=VARCHAR}

SELECT

FROM components p
INNER JOIN components child ON
  child.branch_uuid=p.uuid
  and child.enabled=${_true}
where
  p.enabled=${_true}
  and p.scope='PRJ'
  and child.uuid in

      #{uuid,jdbcType=VARCHAR}

SELECT
FROM components p where
p.qualifier=#{qualifier,jdbcType=VARCHAR}

mapper.sql:
from main_branch_and_portfolios mbp
  INNER JOIN components p on mbp.kee = p.kee and p.branch_uuid = mbp.uuid

  inner join snapshots sa on sa.root_component_uuid=p.uuid
    and sa.status='P' and sa.islast=${_true} and sa.created_at < #{query.analyzedBefore,jdbcType=BIGINT}

left join project_branches pb on pb.uuid = p.branch_uuid
where
  p.enabled=${_true}

    and p.organization_uuid=#{query.organizationUuid,jdbcType=VARCHAR}


    and p.qualifier in

        #{qualifier,jdbcType=VARCHAR}



    and p.kee in

      #{componentKey,jdbcType=BIGINT}



    and p.uuid in

      #{componentUuid,jdbcType=BIGINT}



    and (
      upper(p.name) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'
      or


          upper(p.kee) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'


          p.kee = #{query.nameOrKeyQuery,jdbcType=VARCHAR}


    )



      and p.private=${_true}


      and p.private=${_false}



    and not exists(select 1 from snapshots sp where sp.root_component_uuid=p.uuid)
    and not exists(
      select 1 from snapshots sp
      inner join project_branches pb2 on sp.root_component_uuid = pb2.uuid
      where pb2.project_uuid = pb.project_uuid
    )


    and
    (
        (select max(s.created_at) from snapshots s
        inner join project_branches pb2 on s.root_component_uuid = pb2.uuid
        where pb2.project_uuid = pb.project_uuid
        and s.status='P'
        and s.islast = ${_true}
        ) < #{query.allBranchesAnalyzedBefore,jdbcType=BIGINT}
      or
        exists (
        -- portfolios
        select 1 from snapshots s
        where s.root_component_uuid = p.uuid
        and p.qualifier = 'VW'
        and s.status='P'
        and s.islast = ${_true}
        and s.created_at < #{query.allBranchesAnalyzedBefore,jdbcType=BIGINT})
    )


  and p.created_at >= #{query.createdAfter,jdbcType=TIMESTAMP}

mapper.select:
select

from components p
where
p.branch_uuid = #{branchUuid,jdbcType=VARCHAR}
and p.uuid_path in

    #{uuidPath,jdbcType=VARCHAR}

and p.enabled = ${_true}

mapper.sql:
inner join components base on base.branch_uuid = p.branch_uuid and base.uuid = #{baseUuid}


    and p.uuid_path = #{baseUuidPath,jdbcType=VARCHAR}


    and p.uuid_path like #{baseUuidPath,jdbcType=VARCHAR} ESCAPE '/'

and p.enabled = ${_true}

  and p.qualifier in

    #{qualifier,jdbcType=VARCHAR}



  and p.scope in

    #{scope,jdbcType=VARCHAR}



  and (
    p.kee = #{query.nameOrKeyQuery,jdbcType=VARCHAR}
    or
    upper(p.name) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'
  )

mapper.select:
SELECT p.uuid as "uuid", p.branch_uuid as "branchUuid" FROM components p
where

    p.qualifier=#{qualifier,jdbcType=VARCHAR}

select p.copy_component_uuid
from components p
where
  p.enabled = ${_true}
  and p.branch_uuid = #{rootViewUuid,jdbcType=VARCHAR}


    and p.uuid_path like #{viewUuidLikeQuery,jdbcType=VARCHAR} {escape '\'}


    and p.uuid_path like #{viewUuidLikeQuery,jdbcType=VARCHAR} ESCAPE '\'


  and p.qualifier = 'TRK'
  and p.copy_component_uuid is not null

mapper.update:
update components set
kee = deprecated_kee, copy_component_uuid = b_copy_component_uuid, description = b_description, enabled = b_enabled, uuid_path = b_uuid_path, language = b_language, long_name = b_long_name, name = b_name, path = b_path, qualifier = b_qualifier,
scope = ( case when b_qualifier = 'VW' THEN 'PRJ' when b_qualifier = 'SVW' THEN 'PRJ' when b_qualifier = 'APP' THEN 'PRJ' when b_qualifier = 'DIR' THEN 'DIR' when b_qualifier = 'FIL' THEN 'FIL' when b_qualifier = 'UTS' THEN 'FIL'
else scope end ), b_changed = ${_false},
deprecated_kee = null, b_copy_component_uuid = null, b_description = null, b_enabled = ${_false}, b_language = null, b_long_name = null, b_name = null, b_path = null, b_qualifier = null where branch_uuid = #{branchUuid,jdbcType=VARCHAR} and b_changed = ${_true}

update components set
  private = #{isPrivate,jdbcType=BOOLEAN}
where
  branch_uuid = #{branchUuid,jdbcType=VARCHAR}
  and private <> #{isPrivate,jdbcType=BOOLEAN}

mapper.sql:
select case when exists ( select c.uuid from components c where c.kee in
#{componentKey,jdbcType=VARCHAR}
and c.scope = 'PRJ' and c.qualifier in
#{qualifier,jdbcType=VARCHAR}
) then 1 else 0 end
