mapper.select:
SELECT

FROM components p

  INNER JOIN project_branches pb on pb.uuid = p.branch_uuid

WHERE
p.enabled=${_true}
AND p.kee IN

  #{key,jdbcType=VARCHAR}



    AND pb.kee=#{branch,jdbcType=VARCHAR}
    AND pb.branch_type = 'BRANCH'


    AND pb.kee=#{pullRequest,jdbcType=VARCHAR}
    AND pb.branch_type = 'PULL_REQUEST'


    AND p.main_branch_project_uuid IS NULL

select
from components p where p.uuid in
#{uuid,jdbcType=VARCHAR}

SELECT

FROM components p
INNER JOIN components child ON
  child.root_uuid=p.uuid
  and child.enabled=${_true}
where
  p.enabled=${_true}
  and p.scope='PRJ'
  and child.uuid in

      #{uuid,jdbcType=VARCHAR}

mapper.sql:
INNER JOIN components module ON
  module.branch_uuid = p.branch_uuid
  and module.uuid = #{moduleUuid}
  and module.scope='PRJ' AND module.enabled = ${_true}
where
  p.scope = #{scope,jdbcType=VARCHAR}

    and p.enabled = ${_true}

  and


      p.module_uuid_path LIKE module.module_uuid_path + '%'


      p.module_uuid_path LIKE module.module_uuid_path || '%'

mapper.select:
SELECT
FROM components p where
p.qualifier=#{qualifier,jdbcType=VARCHAR}

mapper.sql:
from components p

  inner join snapshots sa on sa.component_uuid=p.uuid
    and sa.status='P' and sa.islast=${_true} and sa.created_at < #{query.analyzedBefore,jdbcType=BIGINT}

where
  p.enabled=${_true}
  AND p.main_branch_project_uuid is null
  AND p.copy_component_uuid is null

    and p.organization_uuid=#{organizationUuid,jdbcType=VARCHAR}


    and p.qualifier in

        #{qualifier,jdbcType=VARCHAR}



    and p.kee in

      #{componentKey,jdbcType=BIGINT}



    and p.uuid in

      #{componentUuid,jdbcType=BIGINT}



    and (
      upper(p.name) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'
      or


          upper(p.kee) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'


          p.kee = #{query.nameOrKeyQuery,jdbcType=VARCHAR}


    )



      and p.private=${_true}


      and p.private=${_false}



    and not exists(select 1 from snapshots sp where sp.component_uuid=p.uuid)
    and not exists(
      select 1 from snapshots sp
      inner join project_branches pb on sp.component_uuid = pb.uuid
      where pb.project_uuid = p.uuid
    )


    and (
      exists(
        -- branches of projects and applications
        select 1 from snapshots s
        inner join project_branches pb on s.component_uuid = pb.uuid
        where pb.project_uuid = p.uuid
        and s.status='P'
        and s.islast = ${_true}
        and s.created_at >= #{query.anyBranchAnalyzedAfter,jdbcType=BIGINT}
      )
      or exists (
        -- portfolios
        select 1 from snapshots s
        where s.component_uuid = p.uuid
        and s.status='P'
        and s.islast = ${_true}
        and s.created_at >= #{query.anyBranchAnalyzedAfter,jdbcType=BIGINT}
      )
    )


    and (
      exists(
      -- branches of projects and applications
      select 1 from snapshots s
      inner join project_branches pb on s.component_uuid = pb.uuid
      where pb.project_uuid = p.uuid
      and s.status='P'
      and s.islast = ${_true}
      and s.created_at < #{query.anyBranchAnalyzedBefore,jdbcType=BIGINT}
      )
      or exists (
      -- portfolios
      select 1 from snapshots s
      where s.component_uuid = p.uuid
      and s.status='P'
      and s.islast = ${_true}
      and s.created_at < #{query.anyBranchAnalyzedBefore,jdbcType=BIGINT}
      )
    )


    and
    (
        (select max(s.created_at) from snapshots s
        inner join project_branches pb on s.component_uuid = pb.uuid
        where pb.project_uuid = p.uuid
        and s.status='P'
        and s.islast = ${_true}
        ) < #{query.allBranchesAnalyzedBefore,jdbcType=BIGINT}
      or
        exists (
        -- portfolios
        select 1 from snapshots s
        where s.component_uuid = p.uuid
        and p.qualifier = 'VW'
        and s.status='P'
        and s.islast = ${_true}
        and s.created_at < #{query.allBranchesAnalyzedBefore,jdbcType=BIGINT})
    )


  and p.created_at >= #{query.createdAfter,jdbcType=TIMESTAMP}

mapper.select:
select

from components p
where
p.branch_uuid = #{branchUuid,jdbcType=VARCHAR}
and p.uuid_path in

    #{uuidPath,jdbcType=VARCHAR}

and p.enabled = ${_true}

mapper.sql:
inner join components base on base.branch_uuid = p.branch_uuid and base.uuid = #{baseUuid}


    and p.uuid_path = #{baseUuidPath,jdbcType=VARCHAR}


    and p.uuid_path like #{baseUuidPath,jdbcType=VARCHAR} ESCAPE '/'

and p.enabled = ${_true}

  and p.qualifier in

    #{qualifier,jdbcType=VARCHAR}



  and p.scope in

    #{scope,jdbcType=VARCHAR}



  and (
    p.kee = #{query.nameOrKeyQuery,jdbcType=VARCHAR}
    or
    upper(p.name) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'
  )

mapper.select:
SELECT p.uuid as "uuid", p.branch_uuid as "branchUuid" FROM components p
where

    p.qualifier=#{qualifier,jdbcType=VARCHAR}

select p.copy_component_uuid
from components p
where
  p.enabled = ${_true}
  and p.branch_uuid = #{projectViewUuid,jdbcType=VARCHAR}


    and p.module_uuid_path like #{viewUuidLikeQuery,jdbcType=VARCHAR} {escape '\'}


    and p.module_uuid_path like #{viewUuidLikeQuery,jdbcType=VARCHAR} ESCAPE '\'


  and p.qualifier = 'TRK'
  and p.copy_component_uuid is not null

SELECT

FROM components p
INNER JOIN components root ON root.uuid=p.branch_uuid AND root.kee=#{projectKey,jdbcType=VARCHAR}

  INNER JOIN project_branches pb ON pb.uuid=p.branch_uuid



    p.enabled = ${_true}


    AND p.scope=#{scope,jdbcType=VARCHAR}



      AND pb.kee=#{branch,jdbcType=VARCHAR} AND pb.branch_type = 'BRANCH'


      AND pb.kee=#{pullRequest,jdbcType=VARCHAR} AND pb.branch_type = 'PULL_REQUEST'


      AND root.main_branch_project_uuid is null

select
from components p where p.enabled=${_true} and p.copy_component_uuid is null and p.main_branch_project_uuid is null and p.scope = 'PRJ' and p.qualifier in ('TRK','VW','SVW','APP')
and p.branch_uuid = #{projectUuid,jdbcType=VARCHAR}

mapper.update:
update components set
kee = deprecated_kee, copy_component_uuid = b_copy_component_uuid, description = b_description, enabled = b_enabled, uuid_path = b_uuid_path, language = b_language, long_name = b_long_name, module_uuid = b_module_uuid, module_uuid_path = b_module_uuid_path, name = b_name, path = b_path, qualifier = b_qualifier,
scope = ( case when b_qualifier = 'VW' THEN 'PRJ' when b_qualifier = 'SVW' THEN 'PRJ' when b_qualifier = 'APP' THEN 'PRJ' when b_qualifier = 'DIR' THEN 'DIR' when b_qualifier = 'FIL' THEN 'FIL' when b_qualifier = 'UTS' THEN 'FIL'
else scope end ), b_changed = ${_false},
deprecated_kee = null, b_copy_component_uuid = null, b_description = null, b_enabled = ${_false}, b_language = null, b_long_name = null, b_module_uuid = null, b_module_uuid_path = null, b_name = null, b_path = null, b_qualifier = null where branch_uuid = #{branchUuid,jdbcType=VARCHAR} and b_changed = ${_true}

update components set
  private = #{isPrivate,jdbcType=BOOLEAN}
where
  branch_uuid = #{branchUuid,jdbcType=VARCHAR}
  and private <> #{isPrivate,jdbcType=BOOLEAN}

mapper.sql:
select case when exists ( select c.uuid from components c where c.kee in
#{componentKey,jdbcType=VARCHAR}
and c.scope = 'PRJ' and c.qualifier in
#{qualifier,jdbcType=VARCHAR}
) then 1 else 0 end
