mapper.select:
select i.kee as issueKey, i.assignee, i.line, i.resolution, i.severity, i.status, i.effort, i.author_login as authorLogin, i.issue_close_date as issueCloseDate, i.issue_creation_date as issueCreationDate, i.issue_update_date as issueUpdateDate, r.uuid as ruleUuid, r.language as language, i.clean_code_attribute as cleanCodeAttribute, r.clean_code_attribute as ruleCleanCodeAttribute, c.uuid as componentUuid, c.path, c.scope, c.branch_uuid as branchUuid, pb.is_main as isMain, pb.project_uuid as projectUuid, i.tags, i.issue_type as issueType, r.security_standards as securityStandards, c.qualifier, i.code_variants as codeVariants, i.prioritized_rule as prioritizedRule,
from issues i inner join rules r on r.uuid = i.rule_uuid inner join components c on c.uuid = i.component_uuid inner join project_branches pb on c.branch_uuid = pb.uuid left join new_code_reference_issues n on n.issue_key = i.kee left outer join issues_impacts ii on i.kee = ii.issue_key left outer join rules_default_impacts rdi on r.uuid = rdi.rule_uuid left join issues_dependency dep on i.kee = dep.issue_uuid left join cves cve on dep.cve_uuid = cve.uuid
and c.branch_uuid = #{branchUuid,jdbcType=VARCHAR} and i.project_uuid = #{branchUuid,jdbcType=VARCHAR}
and i.kee in
#{key,jdbcType=VARCHAR}
order by i.kee

select
from issues i inner join rules r on r.uuid=i.rule_uuid inner join components p on p.uuid=i.component_uuid inner join components root on root.uuid=i.project_uuid left join new_code_reference_issues n on i.kee = n.issue_key left outer join issues_impacts ii on i.kee = ii.issue_key left outer join rules_default_impacts rdi on r.uuid = rdi.rule_uuid left join issues_dependency dep on i.kee = dep.issue_uuid left join cves cve on dep.cve_uuid = cve.uuid where i.kee in
#{key,jdbcType=VARCHAR}
and i.updated_at <> #{updatedAt,jdbcType=BIGINT}

select i.kee as kee, i.message as message, i.line as line, i.status as status, i.checksum as checksum, i.issue_update_date as issueUpdateDate, r.plugin_rule_key as ruleKey, r.plugin_name as ruleRepo, b.kee as branchKey, b.branch_type as branchType from issues i inner join rules r on r.uuid = i.rule_uuid inner join project_branches b on i.project_uuid = b.uuid where i.component_uuid in
#{key,jdbcType=VARCHAR}
and i.status <> 'CLOSED'

select i.kee from rules r inner join issues i on i.rule_uuid = r.uuid where i.project_uuid=#{componentUuid,jdbcType=VARCHAR} AND i.status <> 'CLOSED'
AND r.plugin_name IN
#{ruleRepository}
AND r.plugin_name NOT IN
#{ruleRepository}
AND r.language IN
#{language}
order by i.kee

select
  i.kee
from rules r
   inner join issues i on i.rule_uuid = r.uuid
where
  i.project_uuid=#{componentUuid,jdbcType=VARCHAR}
  AND i.issue_update_date >= #{changedSince,jdbcType=BIGINT}
  AND i.status <> 'CLOSED'

  AND r.plugin_name IN

      #{ruleRepository}



  AND r.plugin_name NOT IN

      #{ruleRepository}



  AND r.language IN

        #{language}


order by i.kee

select
, p.path as filePath from issues i inner join rules r on r.uuid = i.rule_uuid inner join components p on p.uuid=i.component_uuid left join users u on i.assignee = u.uuid left outer join issues_impacts ii on i.kee = ii.issue_key left outer join rules_default_impacts rdi on r.uuid = rdi.rule_uuid left join issues_dependency dep on i.kee = dep.issue_uuid left join cves cve on dep.cve_uuid = cve.uuid where
i.kee IN
#{key,jdbcType=VARCHAR}
AND i.issue_update_date >= #{changedSince,jdbcType=BIGINT}
order by i.kee

select i.kee
from rules r
    inner join issues i on r.uuid = i.rule_uuid
where
  i.project_uuid = #{queryParams.branchUuid}
  AND issue_update_date >= #{queryParams.changedSince}
  AND i.status = 'CLOSED'

      AND r.plugin_name IN

        #{ruleRepository}



      AND r.plugin_name NOT IN

        #{ruleRepository}



      AND r.language IN

        #{language}

select
i.kee
from issues i
inner join components p on p.uuid=i.component_uuid
inner join components root on root.uuid=i.project_uuid
inner join project_branches pb on pb.uuid=i.project_uuid
left join new_code_reference_issues n on i.kee = n.issue_key

  left join issues_impacts im on i.kee = im.issue_key



    root.kee = #{query.project,jdbcType=VARCHAR}


    AND p.kee = #{query.component,jdbcType=VARCHAR}


    AND pb.kee = #{query.branch,jdbcType=VARCHAR}


    AND pb.kee = #{query.pullRequest,jdbcType=VARCHAR}


    AND i.issue_type IN

      #{type,jdbcType=VARCHAR}



    AND im.software_quality IN

      #{type,jdbcType=VARCHAR}



    AND i.status IN

      #{status,jdbcType=VARCHAR}



      AND i.resolution is not null


    AND i.resolution IN

      #{resolution,jdbcType=VARCHAR}



    AND n.uuid IS NOT NULL


    AND i.issue_creation_date >= #{query.createdAfter,jdbcType=BIGINT}




    order by (select null)
