mapper.sql:
DELETE FROM jira_work_items_resources
WHERE jira_work_item_id IN (
  SELECT wi.id
  FROM jira_work_items wi
  INNER JOIN jira_project_bindings pb ON wi.jira_project_binding_id = pb.id
  INNER JOIN jira_org_bindings ob ON pb.jira_organization_binding_id = ob.id
  WHERE ob.sonar_organization_uuid = #{sonarOrganizationUuid, jdbcType=VARCHAR}
);
DELETE FROM jira_work_items
WHERE jira_project_binding_id IN (
  SELECT pb.id
  FROM jira_project_bindings pb
  INNER JOIN jira_org_bindings ob ON pb.jira_organization_binding_id = ob.id
  WHERE ob.sonar_organization_uuid = #{sonarOrganizationUuid, jdbcType=VARCHAR}
);
DELETE FROM jira_selected_work_types
WHERE jira_project_binding_id IN (
  SELECT pb.id
  FROM jira_project_bindings pb
  INNER JOIN jira_org_bindings ob ON pb.jira_organization_binding_id = ob.id
  WHERE ob.sonar_organization_uuid = #{sonarOrganizationUuid, jdbcType=VARCHAR}
);
DELETE FROM jira_project_bindings
WHERE jira_organization_binding_id IN (
  SELECT ob.id
  FROM jira_org_bindings ob
  WHERE ob.sonar_organization_uuid = #{sonarOrganizationUuid, jdbcType=VARCHAR}
);
DELETE FROM jira_org_bindings
WHERE sonar_organization_uuid = #{sonarOrganizationUuid, jdbcType=VARCHAR};
