mapper.select:
select  from live_measures lm
where
lm.metric_id in #{metricId, jdbcType=INTEGER}
and lm.component_uuid in

  #{componentUuid, jdbcType=VARCHAR}

select  from live_measures lm
inner join metrics m on m.id = lm.metric_id
where
m.name in #{metricKey, jdbcType=VARCHAR}
and lm.component_uuid in

  #{componentUuid, jdbcType=VARCHAR}

select  from live_measures lm
inner join metrics m on m.id = lm.metric_id
where
m.name = #{metricKey, jdbcType=VARCHAR}
and lm.component_uuid = #{componentUuid, jdbcType=VARCHAR}

select sum(sumncloc.maxncloc) from (
  select b.project_uuid as projectUuid, max(lm.value) as maxncloc
  from live_measures lm
  inner join metrics m on m.id = lm.metric_id
  inner join projects p on p.uuid = lm.component_uuid
  inner join project_branches b on b.uuid = p.uuid

    m.name = #{ncloc, jdbcType=VARCHAR}

      and p.private=${_true}

    and p.enabled = ${_true}
    and p.scope = 'PRJ'
    and p.qualifier = 'TRK'
    and p.copy_component_uuid is null
    and p.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
    and b.branch_type = #{branchType, jdbcType=VARCHAR}
    and b.key_type = #{branch, jdbcType=VARCHAR}

      and b.project_uuid <> #{projectUuidToExclude,jdbcType=VARCHAR}


  group by b.project_uuid
) sumncloc

mapper.update:
update live_measures set
value = #{dto.value, jdbcType=DOUBLE},
variation = #{dto.variation, jdbcType=DOUBLE},
text_value = #{dto.textValue, jdbcType=VARCHAR},
measure_data  = #{dto.data, jdbcType=BINARY},
updated_at = #{now, jdbcType=BIGINT}
where
component_uuid = #{dto.componentUuid, jdbcType=VARCHAR}
and metric_id = #{dto.metricId, jdbcType=INTEGER}

insert into live_measures ( uuid, component_uuid, project_uuid, metric_id, value, text_value, variation, measure_data, created_at, updated_at ) values
#{dto.uuidForUpsert, jdbcType=VARCHAR}, #{dto.componentUuid, jdbcType=VARCHAR}, #{dto.projectUuid, jdbcType=VARCHAR}, #{dto.metricId, jdbcType=INTEGER}, #{dto.value, jdbcType=DOUBLE}, #{dto.textValue, jdbcType=VARCHAR}, #{dto.variation, jdbcType=DOUBLE}, #{dto.data, jdbcType=BINARY}, #{now, jdbcType=BIGINT}, #{now, jdbcType=BIGINT}
on conflict(component_uuid, metric_id) do update set value = excluded.value, variation = excluded.variation, text_value = excluded.text_value, measure_data = excluded.measure_data, updated_at = excluded.updated_at where live_measures.value is distinct from excluded.value or live_measures.variation is distinct from excluded.variation or live_measures.text_value is distinct from excluded.text_value or live_measures.measure_data is distinct from excluded.measure_data

mapper.sql:
delete from live_measures
where
  component_uuid = #{componentUuid, jdbcType=VARCHAR}

  and metric_id not in #{metricId,jdbcType=INTEGER}

mapper.select:
select  from live_measures lm
inner join metrics m on m.id = lm.metric_id
where
m.name in #{metricKey, jdbcType=VARCHAR}
and lm.component_uuid = #{componentUuid, jdbcType=VARCHAR}

select  from live_measures lm
inner join projects p on p.uuid = lm.component_uuid




    lm.metric_id in
    #{metricId,jdbcType=INTEGER}

  and p.enabled = ${_true}

    and p.qualifier in

      #{qualifier,jdbcType=VARCHAR}



    and (
    p.kee = #{query.nameOrKeyQuery,jdbcType=VARCHAR}
    or
    upper(p.name) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'
    )



-- Add measures of base component
union all
select  from live_measures lm
inner join projects p on p.uuid = lm.component_uuid and lm.component_uuid = #{baseUuid, jdbcType=VARCHAR}


    lm.metric_id in
    #{metricId,jdbcType=INTEGER}

  and p.enabled = ${_true}

    and p.qualifier in

      #{qualifier,jdbcType=VARCHAR}



    and (
    p.kee = #{query.nameOrKeyQuery,jdbcType=VARCHAR}
    or
    upper(p.name) like #{query.nameOrKeyUpperLikeQuery,jdbcType=VARCHAR} escape '/'
    )
