mapper.select:
select
from organizations org where org.uuid in
#{uuid, jdbcType=VARCHAR}

select * from (
  select rownum as rn, t.* from (
    select


    ORDER BY org.created_at desc
  ) t
) t
where
  t.rn between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}

mapper.sql:
from organizations org

  inner join organization_members om on org.uuid=om.organization_uuid
    and om.user_uuid=#{query.member,jdbcType=VARCHAR}



    org.kee in

        #{key, jdbcType=VARCHAR}



    and exists(
      select 1
      from snapshots s
      inner join components p on p.uuid = s.component_uuid
      where p.organization_uuid = org.uuid
      and p.enabled = ${_true}
      and s.islast = ${_true}
    )


    and exists(
    select 1
      from snapshots s
      inner join components p on p.uuid = s.component_uuid
      where p.organization_uuid = org.uuid
      and p.enabled = ${_true}
      and s.islast = ${_true}
      and s.created_at >= #{query.analyzedAfter,jdbcType=BIGINT}
    )

mapper.select:
select

from organizations org
inner join user_roles ur on ur.user_uuid = #{userUuid,jdbcType=VARCHAR}
  and ur.entity_uuid is null
  and ur.role = #{permission,jdbcType=VARCHAR}
union
select

from organizations org
inner join group_roles g on g.entity_uuid is null
and g.role = #{permission,jdbcType=VARCHAR}
inner join groups_users gu on
gu.group_uuid = g.group_uuid
and gu.user_uuid= #{userUuid,jdbcType=VARCHAR}

select

from organizations org
where org.uuid in
(
select distinct user_orgs.organization_uuid
from (
select organization_uuid
from user_roles
where role = #{permission,jdbcType=VARCHAR} and user_uuid = #{userUuid,jdbcType=VARCHAR}

UNION

select organization_uuid
from group_roles as gr
inner join groups_users as gu on gr.group_uuid = gu.group_uuid
where role = #{permission,jdbcType=VARCHAR} and user_uuid = #{userUuid,jdbcType=VARCHAR}
) AS user_orgs
)

mapper.update:
update organizations
set
  default_group_uuid = #{defaultGroupUuid, jdbcType=VARCHAR},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

update organizations
set
  default_quality_gate_uuid = #{defaultQualityGateUuid, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

update organizations
set
  new_project_private = #{newProjectPrivate, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

mapper.delete:
delete from organizations
where
  uuid = #{uuid, jdbcType=VARCHAR}
