mapper.select:
select
from organizations org where org.uuid in
#{uuid, jdbcType=VARCHAR}

select * from (
  select rownum as rn, t.* from (
    select


    ORDER BY org.created_at desc
  ) t
) t
where
  t.rn between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}

mapper.sql:
from organizations org

  inner join organization_members om on org.uuid=om.organization_uuid
    and om.user_id=#{query.member,jdbcType=INTEGER}



    org.kee in

        #{key, jdbcType=VARCHAR}



    and not exists(
      select 1
      from users u
      where u.organization_uuid = org.uuid
      and u.active = ${_true}
    )


    and exists(
      select 1
      from users u
      where u.organization_uuid = org.uuid
      and u.active = ${_true}
    )


    and exists(
      select 1
      from snapshots s
      inner join projects p on p.uuid = s.component_uuid
      where p.organization_uuid = org.uuid
      and p.enabled = ${_true}
      and s.islast = ${_true}
    )


    and exists(
    select 1
      from snapshots s
      inner join projects p on p.uuid = s.component_uuid
      where p.organization_uuid = org.uuid
      and p.enabled = ${_true}
      and s.islast = ${_true}
      and s.created_at >= #{query.analyzedAfter,jdbcType=BIGINT}
    )

mapper.select:
select
from organizations org inner join user_roles u on u.organization_uuid = org.uuid and u.user_id = #{userId,jdbcType=INTEGER} and u.resource_id is null and u.role = #{permission,jdbcType=VARCHAR} union select
from organizations org inner join group_roles g on g.organization_uuid = org.uuid and g.resource_id is null and g.role = #{permission,jdbcType=VARCHAR} inner join groups_users gu on gu.group_id = g.group_id and gu.user_id = #{userId,jdbcType=INTEGER}

select o.uuid as id, o.kee as kee, o.name as name, t.ncloc as ncloc
from organizations o
left outer join (
select orgUuid, sum(sumncloc.maxncloc) as ncloc from (
select b.project_uuid, p.organization_uuid as orgUuid, max(lm.value) as maxncloc
from live_measures lm
inner join metrics m on m.id = lm.metric_id
inner join projects p on p.uuid = lm.component_uuid
inner join project_branches b on b.uuid = p.uuid
where
m.name = #{ncloc, jdbcType=VARCHAR}
and p.enabled = ${_true}
and p.private = ${_true}
and p.scope = 'PRJ'
and p.qualifier = 'TRK'
and p.copy_component_uuid is null
and p.organization_uuid in #{uuid, jdbcType=VARCHAR}
and b.branch_type = #{branchType, jdbcType=VARCHAR}
and b.key_type = #{branch, jdbcType=VARCHAR}
group by b.project_uuid, p.organization_uuid
) sumncloc
group by orgUuid
) t on t.orgUuid = o.uuid
where
o.uuid in #{uuid, jdbcType=VARCHAR}

mapper.update:
update organizations
set
  default_group_id = #{defaultGroupId, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

update organizations
set
  default_quality_gate_uuid = #{defaultQualityGateUuid, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

update organizations
set
  new_project_private = #{newProjectPrivate, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

mapper.delete:
delete from organizations
where
  uuid = #{uuid, jdbcType=VARCHAR}
