mapper.select:
select
from organizations org where org.uuid in
#{uuid, jdbcType=VARCHAR}

select * from (
  select rownum as rn, t.* from (
    select


    ORDER BY org.created_at desc
  ) t
) t
where
  t.rn between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}

mapper.sql:
from organizations org

  inner join organization_members om on org.uuid=om.organization_uuid
    and om.user_id=#{query.member,jdbcType=INTEGER}



    org.kee in

        #{key, jdbcType=VARCHAR}

mapper.select:
select
from organizations org inner join user_roles u on u.organization_uuid = org.uuid and u.user_id = #{userId,jdbcType=INTEGER} and u.resource_id is null and u.role = #{permission,jdbcType=VARCHAR} union select
from organizations org inner join group_roles g on g.organization_uuid = org.uuid and g.resource_id is null and g.role = #{permission,jdbcType=VARCHAR} inner join groups_users gu on gu.group_id = g.group_id and gu.user_id = #{userId,jdbcType=INTEGER}

mapper.update:
update organizations
set
  default_group_id = #{defaultGroupId, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

update organizations
set
  default_quality_gate_uuid = #{defaultQualityGateUuid, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

update organizations
set
  new_project_private = #{newProjectPrivate, jdbcType=INTEGER},
  updated_at = #{now, jdbcType=BIGINT}
where
  uuid = #{organizationUuid, jdbcType=VARCHAR}

mapper.delete:
delete from organizations
where
  uuid = #{uuid, jdbcType=VARCHAR}
