mapper.select:
select

from organization_members om
where
om.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
and om.user_uuid = #{userUuid, jdbcType=VARCHAR}

select om.user_uuid
from organization_members om
where om.organization_uuid=#{organizationUuid,jdbcType=VARCHAR}

select om.organization_uuid as "organizationUuid"
from organization_members om
where om.user_uuid = #{userUuid, jdbcType=VARCHAR}

SELECT organization_uuid as organizationUuid , user_uuid as userUuid, type as type
FROM organization_members
where organization_uuid=#{organizationUuid,jdbcType=VARCHAR}

mapper.delete:
delete from organization_members
where
organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
and user_uuid = #{userUuid, jdbcType=VARCHAR}

delete from organization_members
where
organization_uuid = #{organizationUuid, jdbcType=VARCHAR}

mapper.update:
UPDATE organization_members
SET type = #{type, jdbcType=VARCHAR}
WHERE user_uuid = (
SELECT uuid FROM users WHERE login = #{login, jdbcType=VARCHAR}
)
AND organization_uuid = (
SELECT uuid FROM organizations
WHERE kee = #{organizationKee, jdbcType=VARCHAR}
)
