mapper.select:
select gu.user_uuid from groups_users gu inner join group_roles gr on gr.group_uuid=gu.group_uuid where gr.entity_uuid=#{entityUuid, jdbcType=VARCHAR} and gr.role=#{role, jdbcType=VARCHAR} and gu.user_uuid in
#{uuid, jdbcType=VARCHAR}
union
select ur.user_uuid from user_roles ur where ur.entity_uuid=#{entityUuid, jdbcType=VARCHAR} and ur.role=#{role, jdbcType=VARCHAR} and ur.user_uuid IN
#{uuid, jdbcType=VARCHAR}
union
select u.uuid from users u where u.uuid in
#{uuid, jdbcType=VARCHAR}
and exists ( select 1 from () entity where entity.uuid =#{entityUuid, jdbcType=VARCHAR} and entity.isPrivate = ${_false} and #{role, jdbcType=VARCHAR} in ('user','codeviewer') )

select ur.role from user_roles ur inner join () entity on entity.uuid = ur.entity_uuid where entity.uuid = #{entityUuid, jdbcType=VARCHAR} and entity.organizationUuid = ur.organization_uuid and ur.user_uuid = #{userUuid, jdbcType=VARCHAR}
union
select gr.role from group_roles gr inner join groups_users gu on gr.group_uuid = gu.group_uuid inner join () entity on entity.uuid = gr.entity_uuid where entity.uuid = #{entityUuid, jdbcType=VARCHAR} and entity.organizationUuid = gr.organization_uuid and gu.user_uuid = #{userUuid, jdbcType=VARCHAR}
union

select u.login
from users u
where
  u.login in #{login, jdbcType=VARCHAR}
  and (
    exists (
      select 1
      from user_roles ur
      inner join () entity on entity.uuid = ur.entity_uuid and entity.organizationUuid = ur.organization_uuid
      where
        entity.kee =  #{entityKey, jdbcType=VARCHAR}
        and ur.role = #{permission, jdbcType=VARCHAR}
        and ur.user_uuid = u.uuid
    ) or exists (
      select 1
      from () entity
      inner join group_roles gr on gr.entity_uuid = entity.uuid and gr.organization_uuid = entity.organizationUuid
      inner join groups_users gu on gu.group_uuid = gr.group_uuid
      where
        entity.kee  =  #{entityKey, jdbcType=VARCHAR}
        and gu.user_uuid = u.uuid
        and gr.role = #{permission, jdbcType=VARCHAR}
    )

      or exists (
        select 1
        from () entity
        where
          entity.kee =  #{entityKey, jdbcType=VARCHAR}
          and entity.isPrivate = ${_false}
      )

  )
