mapper.select:
select p.uuid
from projects p
inner join group_roles gr on p.id = gr.resource_id
where
  gr.role = #{permission, jdbcType=VARCHAR}
  and (gr.group_id is null or exists (
    select 1 from groups_users gu
    where
      gu.user_id = #{userId, jdbcType=INTEGER}
      and gr.group_id = gu.group_id)
  )
  and p.uuid in #{projectUuid, jdbcType=VARCHAR}

union

select p.uuid
from projects p
inner join user_roles ur on p.id = ur.resource_id
where
  ur.role=#{permission, jdbcType=VARCHAR}
  and ur.user_id=#{userId, jdbcType=INTEGER}
  and p.uuid in #{projectUuid, jdbcType=VARCHAR}


  union

  select p.uuid
  from projects p
  where
    p.uuid in #{projectUuid, jdbcType=VARCHAR}
    and p.private = ${_false}

select p.uuid
from projects p
inner join group_roles gr on p.id = gr.resource_id
where
  gr.role=#{permission, jdbcType=VARCHAR}
  and gr.group_id is null
  and p.uuid in #{projectUuid, jdbcType=VARCHAR}


  union

  select p.uuid
  from projects p
  where
  p.uuid in #{projectUuid, jdbcType=VARCHAR}
  and p.private = ${_false}

select gu.user_id from groups_users gu inner join group_roles gr on gr.group_id=gu.group_id where gr.resource_id=#{componentId, jdbcType=BIGINT} and gr.role=#{role, jdbcType=VARCHAR} and gu.user_id in
#{id, jdbcType=BIGINT}
union
select ur.user_id from user_roles ur where ur.resource_id=#{componentId, jdbcType=BIGINT} and ur.role=#{role, jdbcType=VARCHAR} and ur.user_id IN
#{id, jdbcType=BIGINT}
union
select u.id from users u where u.id in
#{id, jdbcType=BIGINT}
and exists ( select 1 from projects p where p.id =#{componentId, jdbcType=BIGINT} and p.private = ${_false} and #{role, jdbcType=VARCHAR} in ('user','codeviewer') )

select ur.role from user_roles ur inner join projects p on p.id = ur.resource_id where p.uuid = #{projectUuid, jdbcType=VARCHAR} and p.organization_uuid = ur.organization_uuid and ur.user_id = #{userId, jdbcType=BIGINT}
union
select gr.role from group_roles gr inner join groups_users gu on gr.group_id = gu.group_id inner join projects p on p.id = gr.resource_id where p.uuid = #{projectUuid, jdbcType=VARCHAR} and p.organization_uuid = gr.organization_uuid and gu.user_id = #{userId, jdbcType=BIGINT}
union

select u.login
from users u
where
  u.login in #{login, jdbcType=VARCHAR}
  and (
    exists (
      select 1
      from user_roles ur
      inner join projects p on p.id = ur.resource_id and p.organization_uuid = ur.organization_uuid
      where
        p.kee =  #{projectKey, jdbcType=VARCHAR}
        and ur.role = #{permission, jdbcType=VARCHAR}
        and ur.user_id = u.id
    ) or exists (
      select 1
      from projects p
      inner join group_roles gr on gr.resource_id = p.id and gr.organization_uuid = p.organization_uuid
      inner join groups_users gu on gu.group_id = gr.group_id
      where
        p.kee  =  #{projectKey, jdbcType=VARCHAR}
        and gu.user_id = u.id
        and gr.role = #{permission, jdbcType=VARCHAR}
    )

      or exists (
        select 1
        from projects p
        where
          p.kee =  #{projectKey, jdbcType=VARCHAR}
          and p.private = ${_false}
      )

  )
