select gr.component_uuid from group_roles gr where gr.role=#{role, jdbcType=VARCHAR} and ( gr.group_uuid is null or exists ( select 1 from groups_users gu where gu.user_uuid = #{userUuid, jdbcType=VARCHAR} and gr.group_uuid = gu.group_uuid ) ) and gr.component_uuid=#{element, jdbcType=VARCHAR}
