mapper.select:
select gu.user_uuid from groups_users gu inner join group_roles gr on gr.group_uuid=gu.group_uuid where gr.component_uuid=#{componentUuid, jdbcType=VARCHAR} and gr.role=#{role, jdbcType=VARCHAR} and gu.user_uuid in
#{uuid, jdbcType=VARCHAR}
union
select ur.user_uuid from user_roles ur where ur.component_uuid=#{componentUuid, jdbcType=VARCHAR} and ur.role=#{role, jdbcType=VARCHAR} and ur.user_uuid IN
#{uuid, jdbcType=VARCHAR}
union
select u.uuid from users u where u.uuid in
#{uuid, jdbcType=VARCHAR}
and exists ( select 1 from components p where p.uuid =#{componentUuid, jdbcType=VARCHAR} and p.private = ${_false} and #{role, jdbcType=VARCHAR} in ('user','codeviewer') )

select ur.role from user_roles ur inner join components p on p.uuid = ur.component_uuid where p.uuid = #{projectUuid, jdbcType=VARCHAR} and p.organization_uuid = ur.organization_uuid and ur.user_uuid = #{userUuid, jdbcType=VARCHAR}
union
select gr.role from group_roles gr inner join groups_users gu on gr.group_uuid = gu.group_uuid inner join components p on p.uuid = gr.component_uuid where p.uuid = #{projectUuid, jdbcType=VARCHAR} and p.organization_uuid = gr.organization_uuid and gu.user_uuid = #{userUuid, jdbcType=VARCHAR}
union

select u.login
from users u
where
  u.login in #{login, jdbcType=VARCHAR}
  and (
    exists (
      select 1
      from user_roles ur
      inner join components p on p.uuid = ur.component_uuid and p.organization_uuid = ur.organization_uuid
      where
        p.kee =  #{projectKey, jdbcType=VARCHAR}
        and ur.role = #{permission, jdbcType=VARCHAR}
        and ur.user_uuid = u.uuid
    ) or exists (
      select 1
      from components p
      inner join group_roles gr on gr.component_uuid = p.uuid and gr.organization_uuid = p.organization_uuid
      inner join groups_users gu on gu.group_uuid = gr.group_uuid
      where
        p.kee  =  #{projectKey, jdbcType=VARCHAR}
        and gu.user_uuid = u.uuid
        and gr.role = #{permission, jdbcType=VARCHAR}
    )

      or exists (
        select 1
        from components p
        where
          p.kee =  #{projectKey, jdbcType=VARCHAR}
          and p.private = ${_false}
      )

  )
