mapper.select:
SELECT count(1) as count, permission, componentId FROM ( SELECT g.name as name, group_role.role as permission, group_role.resource_id as componentId FROM groups g INNER JOIN group_roles group_role ON group_role.group_id=g.id UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, group_role.role as permission, group_role.resource_id as componentId FROM group_roles group_role where group_role.group_id IS NULL ) groups where groups.componentId in
#{id,jdbcType=BIGINT}
GROUP BY groups.permission, groups.componentId

select count(1)
from (
  select distinct sub.groupId
  ) g

mapper.sql:
from ( select g.id as groupId, g.name as name, gr.role as permission, gr.resource_id as componentId, gr.id as id from groups g left join group_roles gr on g.id = gr.group_id where g.organization_uuid = #{query.organizationUuid,jdbcType=VARCHAR}
union all
select 0 as groupId, 'Anyone' as name, gr.role as permission, gr.resource_id as componentId, gr.id as id from group_roles gr
where gr.organization_uuid = #{query.organizationUuid,jdbcType=VARCHAR} and gr.group_id is null
) sub left join projects p on sub.componentId = p.id
and lower(sub.name) like #{query.searchQueryToSqlLowercase,jdbcType=VARCHAR} ESCAPE '/'
and sub.permission is not null
and sub.componentId is null
and p.uuid = #{query.componentUuid,jdbcType=VARCHAR}
and sub.permission = #{query.permission,jdbcType=VARCHAR}

mapper.select:
select sub.groupId as groupId, sub.componentId as resourceId, sub.permission as role, sub.organizationUuid as organizationUuid from ( select gr.group_id as groupId, gr.resource_id as componentId, gr.role as permission, g.name as name, gr.organization_uuid as organizationUuid from group_roles gr inner join groups g ON g.id = gr.group_id where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and gr.group_id is not null
union all
select 0 as groupId, gr.resource_id as componentId, gr.role as permission, 'Anyone' as name, gr.organization_uuid as organizationUuid from group_roles gr where gr.group_id is null and gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} ) sub where sub.groupId in
#{groupId,jdbcType=INTEGER}
and sub.componentId=#{projectId,jdbcType=BIGINT}
and sub.componentId is null

select gr.role from group_roles gr where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and gr.resource_id is null and
gr.group_id = #{groupId,jdbcType=INTEGER}
gr.group_id is null

select gr.role from group_roles gr where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and gr.resource_id = #{projectId,jdbcType=BIGINT} and
gr.group_id = #{groupId,jdbcType=INTEGER}
gr.group_id is null

select gr.group_id as groupId, gr.resource_id as resourceId, gr.role as role, gr.organization_uuid as organizationUuid
from group_roles gr
where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR}
and gr.group_id = #{groupId,jdbcType=INTEGER}

mapper.delete:
