mapper.select:
SELECT count(1) as count, permission, componentUuid FROM ( SELECT g.name as name, group_role.role as permission, group_role.component_uuid as componentUuid FROM groups g INNER JOIN group_roles group_role ON group_role.group_uuid=g.uuid UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, group_role.role as permission, group_role.component_uuid as componentUuid FROM group_roles group_role where group_role.group_uuid IS NULL ) groups where groups.componentUuid in
#{id,jdbcType=VARCHAR}
GROUP BY groups.permission, groups.componentUuid

select count(1)
from (
  select distinct sub.groupUuid
  ) g

mapper.sql:
from (
  select g.uuid as groupUuid, g.name as name, gr.role as permission, gr.component_uuid as componentUuid, gr.uuid as uuid
  from groups g
  left join group_roles gr on g.uuid = gr.group_uuid

    and gr.component_uuid is null


    and gr.component_uuid = #{query.componentUuid,jdbcType=VARCHAR}

where
g.organization_uuid = #{query.organizationUuid,jdbcType=VARCHAR}

union all

select 'Anyone' as groupUuid, 'Anyone' as name, gr.role as permission, gr.component_uuid as componentUuid, gr.uuid as uuid
from group_roles gr


    and gr.component_uuid is null


    and gr.component_uuid = #{query.componentUuid,jdbcType=VARCHAR}


    and gr.organization_uuid = #{query.organizationUuid,jdbcType=VARCHAR} and
    gr.group_uuid is null



) sub
left join components p on sub.componentUuid = p.uuid


    and lower(sub.name) like #{query.searchQueryToSqlLowercase,jdbcType=VARCHAR} ESCAPE '/'



    and sub.permission is not null

      and sub.componentUuid is null


      and p.uuid = #{query.componentUuid,jdbcType=VARCHAR}


      and sub.permission = #{query.permission,jdbcType=VARCHAR}

mapper.select:
select sub.groupUuid as groupUuid, sub.componentUuid as componentUuid, sub.permission as role, sub.organizationUuid as organizationUuid from ( select gr.group_uuid as groupUuid, gr.component_uuid as componentUuid, gr.role as permission, g.name as name, gr.organization_uuid as organizationUuid from group_roles gr inner join groups g ON g.uuid = gr.group_uuid where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and gr.group_uuid is not null
union all
select 'Anyone' as groupUuid, gr.component_uuid as componentUuid, gr.role as permission, 'Anyone' as name, gr.organization_uuid as organizationUuid from group_roles gr where gr.group_uuid is null and gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} ) sub where sub.groupUuid in
#{groupUuid,jdbcType=VARCHAR}
and sub.componentUuid=#{projectUuid,jdbcType=VARCHAR}
and sub.componentUuid is null

select gr.role from group_roles gr where gr.component_uuid is null and
gr.group_uuid = #{groupUuid,jdbcType=VARCHAR}
gr.group_uuid is null

select gr.role from group_roles gr where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and gr.component_uuid is null and
gr.group_uuid = #{groupUuid,jdbcType=VARCHAR}
gr.group_uuid is null

select gr.role from group_roles gr where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and gr.component_uuid = #{projectUuid,jdbcType=VARCHAR} and
gr.group_uuid = #{groupUuid,jdbcType=VARCHAR}
gr.group_uuid is null

select gr.group_uuid as groupUuid, gr.component_uuid as componentUuid, gr.role as role, gr.organization_uuid as organizationUuid
from group_roles gr
where gr.organization_uuid = #{organizationUuid,jdbcType=VARCHAR}
and gr.group_uuid = #{groupUuid,jdbcType=VARCHAR}

mapper.delete:
delete from
  group_roles
where
  component_uuid=#{rootComponentUuid,jdbcType=VARCHAR}


      and group_uuid = #{groupUuid,jdbcType=VARCHAR}


      and group_uuid is null

delete from
  group_roles
where
  component_uuid=#{rootComponentUuid,jdbcType=VARCHAR}
  and role=#{permission,jdbcType=VARCHAR}

delete from group_roles where role=#{permission,jdbcType=VARCHAR} and organization_uuid=#{organizationUuid,jdbcType=VARCHAR} and
component_uuid=#{rootComponentUuid,jdbcType=BIGINT}
component_uuid is null
and
group_uuid=#{groupUuid,jdbcType=VARCHAR}
group_uuid is null
