mapper.update:
UPDATE permission_templates
SET name = #{name}, description = #{description}, key_pattern = #{keyPattern}, updated_at = #{updatedAt}
WHERE uuid = #{uuid}

mapper.delete:
DELETE FROM perm_templates_users
WHERE template_uuid = #{templateUuid}
AND user_uuid = #{userUuid}
AND permission_reference = #{permission}

DELETE FROM perm_templates_groups
WHERE template_uuid = #{templateUuid}
AND permission_reference = #{permission}
AND


    group_uuid = #{groupUuid}


    group_uuid IS NULL

mapper.sql:
FROM users u
LEFT JOIN perm_templates_users ptu ON ptu.user_uuid=u.uuid AND ptu.template_uuid=#{templateUuid}

  u.active = ${_true}

    AND (lower(u.name) like #{query.searchQueryToSqlLowercase} ESCAPE '/'
    OR lower(u.login) like #{query.searchQueryToSqlLowercase} ESCAPE '/'
    OR lower(u.email) like #{query.searchQueryToSqlLowercase} ESCAPE '/')


    and ptu.permission_reference is not null

      and ptu.permission_reference=#{query.permission}

FROM (SELECT g.uuid AS group_uuid, g.name AS name, ptg.permission_reference AS permission, ptg.template_uuid AS templateUuid FROM groups g LEFT JOIN perm_templates_groups ptg ON ptg.group_uuid=g.uuid AND ptg.template_uuid=#{templateUuid} UNION ALL SELECT 'Anyone' AS group_uuid, 'Anyone' AS name, ptg.permission_reference AS permission, ptg.template_uuid AS templateUuid FROM groups g LEFT JOIN perm_templates_groups ptg ON ptg.template_uuid=#{templateUuid}
ptg.group_uuid IS NULL
) groups
AND LOWER(groups.name) LIKE #{query.searchQueryToSqlLowercase} ESCAPE '/'
AND groups.permission IS NOT NULL AND groups.templateUuid=#{templateUuid}
AND groups.permission=#{query.permission}

mapper.select:
SELECT

FROM perm_templates_users ptu
INNER JOIN users u ON u.uuid = ptu.user_uuid AND u.active = ${_true}

  AND ptu.template_uuid = #{templateUuid}

    AND u.login IN 
      #{login}

SELECT sub.uuid, sub.templateUuid, sub.permission, sub.groupUuid, sub.groupName, sub.createdAt, sub.updatedAt FROM ( SELECT ptg.uuid, ptg.template_uuid as templateUuid, ptg.permission_reference AS permission, ptg.group_uuid AS groupUuid, g.name AS groupName, ptg.created_at as createdAt, ptg.updated_at as updatedAt FROM perm_templates_groups ptg INNER JOIN groups g ON g.uuid=ptg.group_uuid UNION ALL SELECT ptg.uuid, ptg.template_uuid as templateUuid, ptg.permission_reference AS permission, 'Anyone' AS groupUuid, 'Anyone' AS groupName, ptg.created_at as createdAt, ptg.updated_at as updatedAt FROM perm_templates_groups ptg WHERE ptg.group_uuid IS NULL ) sub
sub.templateUuid=#{templateUuid}
AND sub.groupName IN #{group,jdbcType=VARCHAR}

-- from template users select ptu.permission_reference as permission_key from perm_templates_users ptu
and ptu.user_uuid=#{userUuid} and ptu.template_uuid=#{templateUuid}
UNION -- from template groups except anyone group select ptg.permission_reference as permission_key from perm_templates_groups ptg inner join groups_users gu on ptg.group_uuid = gu.group_uuid
and gu.user_uuid=#{userUuid} and ptg.template_uuid=#{templateUuid}
UNION -- from template characteristics select ptc.permission_key as permission_key from perm_tpl_characteristics ptc
and with_project_creator = ${_true} and ptc.template_uuid = #{templateUuid}
UNION
-- from anyone group select ptg.permission_reference as permission_key from perm_templates_groups ptg where ptg.template_uuid=#{templateUuid} and ptg.group_uuid IS NULL

SELECT ptu.template_uuid as templateUuid, ptu.permission_reference as permission, count(u.login) as count FROM users u INNER JOIN perm_templates_users ptu ON ptu.user_uuid=u.uuid AND ptu.template_uuid in
#{id}
AND u.active = ${_true}
GROUP BY ptu.template_uuid, ptu.permission_reference

SELECT count(1) as count, permission, templateUuid FROM (SELECT g.name as name, ptg.permission_reference as permission, ptg.template_uuid as templateUuid FROM groups g INNER JOIN perm_templates_groups ptg ON ptg.group_uuid=g.uuid UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, ptg.permission_reference as permission, ptg.template_uuid as templateUuid FROM perm_templates_groups ptg
AND ptg.group_uuid IS NULL
) groups
AND groups.templateUuid in
#{id}
GROUP BY groups.permission, groups.templateUuid

select count(1)
from perm_templates_groups ptg
where ptg.template_uuid = #{templateUuid}
and ptg.permission_reference = #{permission}
and

  ptg.group_uuid is null


  ptg.group_uuid = #{groupUuid}
