mapper.select:
SELECT u.login as "login", ${_true} as "global", u.email as "email" FROM users u INNER JOIN properties p ON p.user_uuid = u.uuid and p.prop_key = #{notifKey,jdbcType=VARCHAR} and p.text_value = 'true' and p.component_uuid IS NULL WHERE u.email is not null
and u.login in #{login,jdbcType=VARCHAR}
UNION
SELECT u.login as "login", ${_false} as "global", u.email as "email" FROM users u INNER JOIN components c on c.kee = #{projectKey,jdbcType=VARCHAR} INNER JOIN properties p ON p.user_uuid = u.uuid and p.prop_key = #{notifKey,jdbcType=VARCHAR} and p.text_value = 'true' and p.component_uuid = c.uuid WHERE u.email is not null
and u.login in #{login,jdbcType=VARCHAR}

