mapper.select:
SELECT u.login as "login", ${_true} as "global", u.email as "email" FROM users u INNER JOIN properties p ON p.user_uuid = u.uuid and p.prop_key = #{notifKey,jdbcType=VARCHAR} and p.text_value = #{notifValue,jdbcType=VARCHAR} and p.entity_uuid IS NULL WHERE u.email is not null
and u.login in #{login,jdbcType=VARCHAR}
UNION
SELECT u.login as "login", ${_false} as "global", u.email as "email" FROM users u INNER JOIN projects proj on proj.kee = #{projectKey,jdbcType=VARCHAR} INNER JOIN properties p ON p.user_uuid = u.uuid and p.prop_key = #{notifKey,jdbcType=VARCHAR} and p.text_value = #{notifValue,jdbcType=VARCHAR} and p.entity_uuid = proj.uuid WHERE u.email is not null
and u.login in #{login,jdbcType=VARCHAR}

select

from
  properties p
where
  p.prop_key=#{key}

    and p.entity_uuid is null


    and p.entity_uuid=#{entityUuid}


    and p.user_uuid is null


    and p.user_uuid=#{userUuid,jdbcType=VARCHAR}

select
from properties p where p.prop_key in
#{key}
and p.entity_uuid in
#{entityUuid}
and p.user_uuid is null

select

from
  properties p


    and p.prop_key=#{query.key,jdbcType=VARCHAR}


    and p.entity_uuid=#{query.entityUuid,jdbcType=BIGINT}


    and p.user_uuid=#{query.userUuid,jdbcType=VARCHAR}

select

from properties p

  p.prop_key = #{key,jdbcType=VARCHAR}
  and p.text_value like #{value,jdbcType=VARCHAR}

mapper.delete:
delete from properties
where
  prop_key=#{key}


    and entity_uuid=#{entityUuid}
    and user_uuid=#{userUuid,jdbcType=VARCHAR}


    and entity_uuid=#{entityUuid}
    and user_uuid is null


    and entity_uuid is null
    and user_uuid=#{userUuid,jdbcType=VARCHAR}


    and entity_uuid is null
    and user_uuid is null

delete from properties
where
  prop_key=#{key}
  and entity_uuid=#{entityUuid}
  and user_uuid is null

delete from properties


    and prop_key=#{query.key,jdbcType=VARCHAR}


    and entity_uuid=#{query.entityUuid,jdbcType=BIGINT}


    and user_uuid=#{query.userUuid,jdbcType=VARCHAR}

DELETE FROM properties

  AND prop_key = #{key,jdbcType=VARCHAR}
  AND text_value LIKE #{value,jdbcType=VARCHAR}

mapper.select:
select
from properties p where uuid in
#{uuid}

mapper.update:
update properties set
  prop_key=#{newKey}
where
  prop_key=#{oldKey}
