mapper.delete:
delete from active_rules
where
  uuid=#{uuid, jdbcType=BIGINT}

mapper.select:
select

from active_rules a

where
rp.uuid = #{ruleProfileUuid, jdbcType=VARCHAR}
and r.plugin_rule_key = #{rule, jdbcType=VARCHAR}
and r.plugin_name = #{repository, jdbcType=VARCHAR}

select
from active_rules a
where rp.uuid = #{ruleProfileUuid, jdbcType=VARCHAR}

select

from active_rules a

where
r.uuid in #{ruleUuid, jdbcType=VARCHAR}
and rp.uuid in #{ruleProfileUuid, jdbcType=VARCHAR}

select r.plugin_rule_key as "rulefield", r.plugin_name as "repository" from active_rules ar inner join rules_profiles rp on rp.uuid = ar.profile_uuid inner join org_qprofiles oq on oq.rules_profile_uuid = rp.uuid and oq.uuid in
#{qprofile_uuid, jdbcType=VARCHAR}
inner join rules r on r.uuid = ar.rule_uuid

select
from active_rules a
where r.uuid = #{ruleUuid, jdbcType=VARCHAR}

select
from active_rules a inner join rules_profiles rp on rp.uuid = a.profile_uuid inner join org_qprofiles oqp on oqp.rules_profile_uuid = rp.uuid inner join rules r on r.uuid = a.rule_uuid where a.rule_uuid in
#{ruleUuid, jdbcType=VARCHAR}

mapper.update:
UPDATE active_rule_parameters SET
value=#{value, jdbcType=VARCHAR}
WHERE uuid=#{uuid, jdbcType=VARCHAR}

mapper.delete:
delete from active_rule_parameters where exists ( select 1 from active_rules ar inner join rules_profiles rp on rp.uuid = ar.profile_uuid where ar.uuid = active_rule_uuid and rp.uuid in
#{rulesProfileUuid, jdbcType=VARCHAR}
)

mapper.select:
select

from active_rule_parameters p

  p.active_rule_uuid=#{uuid, jdbcType=VARCHAR}

select

from active_rule_parameters p


    p.active_rule_uuid=#{uuid, jdbcType=VARCHAR}

select oqp.uuid as "key", count(ar.uuid) as "value"
from active_rules ar
inner join rules_profiles rp on rp.uuid = ar.profile_uuid
inner join org_qprofiles oqp on oqp.rules_profile_uuid = rp.uuid
inner join rules r on r.uuid = ar.rule_uuid


    oqp.uuid = #{profileUuid, jdbcType=VARCHAR}



      and r.status != 'REMOVED'


      and r.status = #{ruleStatus, jdbcType=VARCHAR}



    and ar.inheritance = #{inheritance, jdbcType=VARCHAR}


group by oqp.uuid
