mapper.select:
select

from qprofile_edit_users qeu
where
qeu.user_id = #{userId, jdbcType=INTEGER}
and qeu.qprofile_uuid = #{qProfileUuid, jdbcType=VARCHAR}

SELECT u.id as userId, u.name as name, qeu.uuid as uuid

ORDER BY u.name ASC
LIMIT #{pagination.pageSize,jdbcType=INTEGER}
OFFSET #{pagination.offset,jdbcType=INTEGER}

select * from (
  select rownum as rn, t.* from (
    select u.id as userId, u.name as name, qeu.uuid as uuid

    order by u.name ASC
  ) t
) t
where
t.rn between #{pagination.startRowNumber,jdbcType=INTEGER} and #{pagination.endRowNumber,jdbcType=INTEGER}

mapper.sql:
FROM users u
LEFT JOIN qprofile_edit_users qeu ON qeu.user_id=u.id AND qeu.qprofile_uuid=#{query.qProfileUuid, jdbcType=VARCHAR}
INNER JOIN organization_members om ON u.id=om.user_id AND om.organization_uuid=#{query.organizationUuid, jdbcType=VARCHAR}



      AND qeu.uuid IS NOT NULL


      AND qeu.uuid IS NULL



    AND (
    lower(u.name) like #{query.querySqlLowercase} ESCAPE '/'
    or u.login like #{query.querySql} ESCAPE '/')

  AND u.active=${_true}

mapper.select:
SELECT qeu.qprofile_uuid as qProfileUuid
FROM qprofile_edit_users qeu
INNER JOIN org_qprofiles oq ON qeu.qprofile_uuid=oq.uuid AND oq.organization_uuid=#{organizationUuid, jdbcType=VARCHAR}

  qeu.user_id=#{userId, jdbcType=INTEGER}

mapper.delete:
delete from qprofile_edit_users
where qprofile_uuid = #{qProfileUuid, jdbcType=VARCHAR}
and user_id = #{userId, jdbcType=INTEGER}

delete from qprofile_edit_users

  user_id=#{userId, jdbcType=INTEGER}
  and qprofile_uuid in (
    select oq.uuid
    from org_qprofiles oq
    where oq.organization_uuid=#{organizationUuid, jdbcType=VARCHAR}
  )
