mapper.select:
select
from active_rules a inner join rules_profiles rp on rp.uuid = a.profile_uuid inner join org_qprofiles oqp on oqp.rules_profile_uuid = rp.uuid inner join rules r on r.uuid = a.rule_uuid and r.status != 'REMOVED' left join rules rt on rt.uuid = r.template_uuid left join rules_metadata rm on rm.rule_uuid = r.uuid and rm.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
where oqp.uuid = #{uuid, jdbcType=VARCHAR}

select

from active_rule_parameters p

p.active_rule_uuid in #{activeRuleUuid,jdbcType=VARCHAR}
