mapper.select:
SELECT

FROM rules_profiles p
WHERE p.name=#{name, jdbcType=VARCHAR}
AND p.language=#{language, jdbcType=VARCHAR}

  AND p.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}

SELECT

FROM rules_profiles p
WHERE p.name=#{name, jdbcType=VARCHAR}
AND p.language IN #{language, jdbcType=VARCHAR}

  AND p.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}

SELECT

FROM rules_profiles p
WHERE p.kee=#{id}

SELECT
FROM rules_profiles p WHERE p.kee in
#{key}

SELECT

FROM rules_profiles p
WHERE p.id=#{id}

SELECT

FROM rules_profiles p
INNER JOIN rules_profiles child ON child.parent_kee=p.kee and child.id=#{id}

SELECT

FROM rules_profiles p

  p.is_default=${_true}
  AND p.language=#{language}

SELECT

FROM rules_profiles p
WHERE p.is_default=${_true}
AND p.language in #{language, jdbcType=VARCHAR}

  AND p.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}

SELECT projects.id as id, projects.name as name, projects.kee as kee, projects.uuid as uuid
FROM projects projects
JOIN project_qprofiles pp ON pp.project_uuid = projects.uuid
JOIN rules_profiles prof ON pp.profile_key = prof.kee

  AND prof.name = #{profileName}
  AND prof.language = #{language}

SELECT pp.id as id, pj.id as projectId, pj.uuid as projectUuid, pj.kee as projectKey, pj.name as projectName, pp.profile_key as profileKey FROM projects pj JOIN project_qprofiles pp ON pp.project_uuid = pj.uuid AND pp.profile_key = #{profileKey}
AND pj.scope='PRJ' AND pj.qualifier='TRK' AND UPPER(pj.name) LIKE #{nameQuery}
ORDER BY pj.name ASC

SELECT pp.id as id, pj.id as projectId, pj.uuid as projectUuid, pj.kee as projectKey, pj.name as projectName, pp.profile_key as profileKey FROM projects pj LEFT JOIN project_qprofiles pp ON pp.project_uuid = pj.uuid AND pp.profile_key = #{profileKey}
AND pj.scope='PRJ' AND pj.qualifier='TRK' AND UPPER(pj.name) LIKE #{nameQuery} AND pp.profile_key IS NULL
ORDER BY pj.name ASC

SELECT pp.id as id, pj.id as projectId, pj.uuid as projectUuid, pj.kee as projectKey, pj.name as projectName, pp.profile_key as profileKey FROM projects pj LEFT JOIN project_qprofiles pp ON pp.project_uuid = pj.uuid AND pp.profile_key = #{profileKey}
AND pj.scope='PRJ' AND pj.qualifier='TRK' AND UPPER(pj.name) LIKE #{nameQuery}
ORDER BY pj.name ASC

SELECT

FROM rules_profiles p
JOIN project_qprofiles pp ON pp.profile_key=p.kee
JOIN projects project ON pp.project_uuid=project.uuid
AND project.id=#{projectId}
WHERE p.language=#{language}

SELECT

FROM rules_profiles p
JOIN project_qprofiles pp ON pp.profile_key=p.kee
JOIN projects project ON pp.project_uuid=project.uuid AND project.kee=#{projectKey}
WHERE p.language=#{language}

SELECT

FROM rules_profiles p
JOIN project_qprofiles pp ON pp.profile_key=p.kee
JOIN projects project ON pp.project_uuid=project.uuid AND project.kee=#{projectKey}
WHERE p.language in #{language, jdbcType=VARCHAR}

  AND project.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
  AND p.organization_uuid = #{organizationUuid, jdbcType=VARCHAR}
