mapper.insert:
insert into issue_stats_by_rule_key ( aggregation_id, aggregation_type, rule_key, issue_count, rating, hotspot_count, hotspots_reviewed ) values (
#{aggregationId}, 'PROJECT', #{item.ruleKey}, #{item.issueCount}, #{item.rating}, #{item.hotspotCount}, #{item.hotspotsReviewed}
)

mapper.delete:
delete from issue_stats_by_rule_key
where aggregation_id = #{aggregationId}
  and aggregation_type = 'PROJECT'
