mapper.select:
select

from issue_stats_by_rule_key
where aggregation_id = #{aggregationId}
  and aggregation_type = #{aggregationType}

select

from issue_stats_by_rule_key
where aggregation_id IN

  #{aggregationId}

  and aggregation_type = #{aggregationType}

select

from issue_stats_by_rule_key
where aggregation_id = #{aggregationId}
and rule_key = #{ruleKey}

mapper.delete:
delete from issue_stats_by_rule_key
where aggregation_id = #{aggregationId}
  and aggregation_type = #{aggregationType}

delete from issue_stats_by_rule_key
where aggregation_id = #{aggregationId}
  and aggregation_type = #{aggregationType}
  and rule_key = #{ruleKey}
