mapper.select:
select

from groups g
where g.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and g.name = #{name,jdbcType=VARCHAR}

select
from groups g where g.uuid in
#{uuid,jdbcType=VARCHAR}

select
from groups g where g.organization_uuid = #{organizationUuid,jdbcType=VARCHAR} and g.name in
#{name,jdbcType=VARCHAR}

mapper.update:
UPDATE groups SET
name=#{name,jdbcType=VARCHAR},
description=#{description,jdbcType=VARCHAR},
updated_at=#{updatedAt,jdbcType=TIMESTAMP}
WHERE uuid=#{uuid}

sql.where:
g.organization_uuid = #{query.organizationUuid,jdbcType=VARCHAR}

    AND upper(g.name) like #{query.searchText,jdbcType=VARCHAR} escape '/'


    AND ${query.isManagedSqlClause}
