mapper.sql:
FROM users u LEFT JOIN groups_users gu ON gu.user_uuid=u.uuid AND gu.group_uuid=#{groupUuid}
AND gu.group_uuid IS NOT NULL
AND gu.group_uuid IS NULL
AND ( lower(u.name) like #{query.memberSearchSqlLowercase} ESCAPE '/' or u.login like #{query.memberSearchSql} ESCAPE '/' or u.email like #{query.memberSearchSql} ESCAPE '/' )
AND u.active=${_true}

mapper.select:
select group_uuid
from groups_users
where user_uuid = #{userUuid}
