mapper.select:
SELECT

FROM users u
WHERE u.login=#{login}

SELECT

FROM users u
WHERE
u.login=#{scmAccount}
OR u.email=#{scmAccount}
OR u.scm_accounts like #{likeScmAccount}

SELECT

FROM users u
WHERE u.id=#{id}

SELECT

FROM users u
WHERE u.login=#{id} AND u.active=${_true}

SELECT
FROM users u WHERE u.login in
#{login}

SELECT
FROM users u WHERE u.id in
#{id}

SELECT

FROM users u


    u.login IN

      #{login}



    AND u.active=${_true}


    AND (u.login LIKE #{searchTextSql} ESCAPE '/' OR u.name LIKE #{searchTextSql} ESCAPE '/')


    AND u.is_root = ${_true}


    AND u.is_root = ${_false}


ORDER BY u.name

SELECT

FROM users u
WHERE lower(u.email)=#{email, jdbcType=VARCHAR}
AND u.active=${_true}

select
  count(1)
from
  users u
where
  u.active = ${_true}
  and u.is_root = ${_true}
  and u.login <> #{login}

mapper.update:
update users set
  homepage_type = null,
  homepage_parameter = null,
  updated_at = #{now, jdbcType=BIGINT}
where
  homepage_type = #{homepageType, jdbcType=VARCHAR}
  and homepage_parameter = #{homepageParameter, jdbcType=VARCHAR}

update users set
  is_root = #{root, jdbcType=BOOLEAN},
  updated_at = #{now, jdbcType=BIGINT}
where
  login = #{login, jdbcType=VARCHAR}
  and active = ${_true}
