FROM projects p
INNER JOIN snapshots s ON s.project_id=p.id AND s.islast=${_true}
INNER JOIN projects child ON child.root_id=p.id AND child.enabled=${_true}

  AND p.enabled=${_true}
  AND p.scope='PRJ'
  AND child.kee in

    #{key}

