mapper.select:
SELECT project_authorization.project as "project", project_authorization.login as "permissionUser", project_authorization.permission_group as "permissionGroup", project_authorization.permission_role as "permissionRole", project_authorization.updated_at as "updatedAt" FROM ( -- users SELECT projects.uuid AS project, projects.authorization_updated_at AS updated_at, users.login AS login, NULL AS permission_group, user_roles.role as permission_role FROM projects INNER JOIN user_roles ON user_roles.resource_id = projects.id AND user_roles.role = #{permission} INNER JOIN users ON users.id = user_roles.user_id WHERE projects.authorization_updated_at >= #{date} AND projects.qualifier = 'TRK' AND projects.copy_resource_id is NULL
AND projects.uuid = #{project}
UNION -- groups without Anyone SELECT projects.uuid AS project, projects.authorization_updated_at AS updated_at, NULL AS login, groups.name AS permission_group, group_roles.role as permission_role FROM projects INNER JOIN group_roles ON group_roles.resource_id = projects.id AND group_roles.role = #{permission} INNER JOIN groups ON groups.id = group_roles.group_id WHERE projects.authorization_updated_at >= #{date} AND projects.qualifier = 'TRK' AND projects.copy_resource_id is NULL
AND projects.uuid = #{project}
AND group_id IS NOT NULL UNION -- Anyone groups SELECT projects.uuid AS project, projects.authorization_updated_at AS updated_at, NULL AS login, #{anyone} AS permission_group, group_roles.role as permission_role FROM projects INNER JOIN group_roles ON group_roles.resource_id = projects.id AND group_roles.role = #{permission} WHERE projects.authorization_updated_at >= #{date} AND projects.qualifier = 'TRK' AND projects.copy_resource_id is NULL
AND projects.uuid = #{project}
AND group_roles.group_id IS NULL ) project_authorization
