mapper.select:
select
from issues i inner join rules r on r.id=i.rule_id inner join projects p on p.uuid=i.component_uuid inner join projects root on root.uuid=i.project_uuid where i.kee in
#{key}

select
from issues i inner join rules r on r.id=i.rule_id inner join projects p on p.uuid=i.component_uuid inner join projects root on root.uuid=i.project_uuid
and i.action_plan_key=#{action_plan}

SELECT
FROM issues i INNER JOIN projects p on p.uuid=i.component_uuid INNER JOIN rules r on r.id=i.rule_id WHERE p.kee=#{componentKey} AND i.resolution IS NULL
AND i.issue_creation_date >= #{createdAt}
