mapper.select:
SELECT

FROM metrics m

  AND m.name=#{key}

SELECT

FROM metrics m

  AND m.enabled=${_true}


      AND m.user_managed=${_true}


      AND m.user_managed=${_false}



ORDER BY UPPER(m.short_name)

select distinct domain
from metrics m
where m.domain is not null and m.enabled=${_true}

mapper.update:
update metrics
set enabled=${_false}

  AND user_managed=${_true}
  AND id in

    #{id}
