mapper.select:
SELECT name, permission FROM (SELECT g.name as name, group_role.role as permission FROM groups g LEFT JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{query.permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group SELECT #{anyoneGroup} as name, group_role.role as permission FROM group_roles group_role
AND group_role.group_id IS NULL AND group_role.role=#{query.permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups
AND (UPPER(groups.name) LIKE #{query.searchSql} ESCAPE '/')
ORDER BY groups.name
