mapper.select:
select id, kee, name, language
from rules_profiles
where id=#{id}

mapper.update:
update project_measures
set text_value=#{json}, value=null
where id=#{measureId}

update rules_profiles
set created_at=#{createdAt}, updated_at=#{updatedAt}, rules_updated_at=#{rulesUpdatedAt}
where id=#{profileId}
