mapper.select:
select

from ce_activity ca
where ca.uuid=#{uuid}

select

from ce_activity ca


    and ca.is_last=${_true}


    and ca.component_uuid=#{query.componentUuid}


    and ca.status=#{query.status}


    and ca.task_type=#{query.type}


    and ca.submitted_at >= #{query.minSubmittedAt}


    and ca.finished_at <= #{query.maxFinishedAt}


order by ca.id desc

select count(ca.id)
from ce_activity ca


    and ca.is_last=${_true}


    and ca.component_uuid=#{query.componentUuid}


    and ca.status=#{query.status}


    and ca.task_type=#{query.type}


    and ca.submitted_at >= #{query.minSubmittedAt}


    and ca.finished_at <= #{query.maxFinishedAt}

mapper.update:
update ce_activity
set is_last=${_false},
updated_at=#{updatedAt,jdbcType=BIGINT}
where is_last=${_true} and is_last_key=#{isLastKey}

update ce_activity
set is_last=${_true},
updated_at=#{updatedAt,jdbcType=BIGINT}
where uuid=#{uuid}
