mapper.select:
SELECT


ORDER BY ca.id desc
LIMIT #{pageSize} OFFSET #{offset}

mapper.sql:
from ce_activity ca
left outer join ce_scanner_context csc on csc.task_uuid = ca.uuid


    and ca.is_last=${_true}


    and ca.component_uuid in

      #{cUuid}



    and ca.status in

      #{status}



    and ca.task_type=#{query.type}


    and ca.submitted_at >= #{query.minSubmittedAt}


    and ca.executed_at <= #{query.maxExecutedAt}

mapper.select:
select
  count(1)
from
  ce_activity
where
  status=#{status}
  and is_last=${_true}

    and component_uuid=#{componentUuid}

mapper.update:
update ce_activity
set is_last=${_false},
updated_at=#{updatedAt,jdbcType=BIGINT}
where is_last=${_true} and is_last_key=#{isLastKey}
