mapper.select:
select

from projects base
inner join projects p on p.project_uuid = base.project_uuid
where
base.uuid = #{query.baseUuid}
and base.uuid_path like #{baseUuidPathLike}
and p.uuid != base.uuid
and p.enabled = ${_true}
order by ${query.sqlSort}

