mapper.select:
SELECT DISTINCT duplication_block.id as id, duplication_block.snapshot_id as snapshotId, duplication_block.component_uuid as componentUuid, duplication_block.project_snapshot_id as projectSnapshotId, duplication_block.hash as hash, duplication_block.index_in_file as indexInFile, duplication_block.start_line as startLine, duplication_block.end_line as endLine, file_component.kee as componentKey FROM duplications_index duplication_block INNER JOIN snapshots snapshot ON duplication_block.snapshot_id=snapshot.id AND snapshot.islast=${_true} INNER JOIN projects file_component ON file_component.uuid=duplication_block.component_uuid AND file_component.language=#{language} AND file_component.enabled=${_true}
AND duplication_block.hash in #{hash}
AND duplication_block.project_snapshot_id <> #{projectSnapshotId}
