mapper.sql:
FROM users u
LEFT JOIN user_roles user_role ON user_role.user_id=u.id
AND user_role.role=#{query.permission}

  AND user_role.resource_id=#{componentId}


  AND user_role.resource_id IS NULL


  u.active = ${_true}


      AND user_role.role IS NOT NULL


      AND user_role.role IS NULL



    AND (UPPER(u.name) LIKE #{query.searchSql} ESCAPE '/')

from users u left join user_roles ur ON ur.user_id=u.id left join projects p on ur.resource_id = p.id
and u.active = ${_true}
and u.login in
#{login}
and lower(u.name) like #{query.searchQueryToSql} ESCAPE '/'
and ur.role is not null
and ur.resource_id is null
and ur.resource_id is not null
and ur.role=#{query.permission}

mapper.select:
SELECT user_role.resource_id as componentId, user_role.role as permission, count(u.login) as count FROM users u INNER JOIN user_roles user_role ON user_role.user_id=u.id AND user_role.resource_id in
#{id}
u.active = ${_true}
GROUP BY user_role.resource_id, user_role.role

SELECT count(1) as count, permission, componentId FROM (SELECT g.name as name, group_role.role as permission, group_role.resource_id as componentId FROM groups g INNER JOIN group_roles group_role ON group_role.group_id=g.id UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, group_role.role as permission, group_role.resource_id as componentId FROM group_roles group_role
AND group_role.group_id IS NULL
) groups
groups.componentId in
#{id}
GROUP BY groups.permission, groups.componentId

SELECT count(1) FROM (SELECT g.name as name FROM groups g INNER JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name FROM group_roles group_role
AND group_role.role=#{permission} AND group_role.group_id IS NULL
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups

SELECT id, name, description, permission FROM (SELECT g.id as id, g.name as name, g.description as description, group_role.role as permission FROM groups g LEFT JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{query.permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group permission SELECT 0 as id, #{anyoneGroup} as name, NULL as description, group_role.role as permission FROM group_roles group_role
AND group_role.role=#{query.permission} AND group_role.group_id IS NULL
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups
AND (UPPER(groups.name) LIKE #{query.searchSql} ESCAPE '/')
ORDER BY groups.name
