mapper.sql:
FROM users u
LEFT JOIN user_roles user_role ON user_role.user_id=u.id
AND user_role.role=#{query.permission}

  AND user_role.resource_id=#{componentId}


  AND user_role.resource_id IS NULL


  u.active = ${_true}


      AND user_role.role IS NOT NULL


      AND user_role.role IS NULL



    AND (UPPER(u.name) LIKE #{query.searchSql} ESCAPE '/')

mapper.select:
SELECT id, name, description, permission FROM (SELECT g.id as id, g.name as name, g.description as description, group_role.role as permission FROM groups g LEFT JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{query.permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group permission SELECT 0 as id, #{anyoneGroup} as name, NULL as description, group_role.role as permission FROM group_roles group_role
AND group_role.role=#{query.permission} AND group_role.group_id IS NULL
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups
AND (UPPER(groups.name) LIKE #{query.searchSql} ESCAPE '/')
ORDER BY groups.name

SELECT count(name) FROM (SELECT g.name as name FROM groups g LEFT JOIN group_roles group_role ON group_role.group_id=g.id AND group_role.role=#{query.permission}
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name FROM group_roles group_role
AND group_role.role=#{query.permission} AND group_role.group_id IS NULL
AND group_role.resource_id=#{componentId}
AND group_role.resource_id IS NULL
) groups
AND (UPPER(groups.name) LIKE #{query.searchSql} ESCAPE '/')
