mapper.update:
UPDATE permission_templates
SET name = #{name}, description = #{description}, key_pattern = #{keyPattern}, updated_at = #{updatedAt}
WHERE id = #{id}

mapper.delete:
DELETE FROM perm_templates_users
WHERE template_id = #{templateId}
AND user_id = #{userId}
AND permission_reference = #{permission}

DELETE FROM perm_templates_groups
WHERE template_id = #{templateId}
AND permission_reference = #{permission}
AND


    group_id = #{groupId}


    group_id IS NULL

mapper.sql:
FROM users u
LEFT JOIN perm_templates_users ptu ON ptu.user_id=u.id
AND ptu.permission_reference=#{query.permission}
AND ptu.template_id=#{templateId}

  u.active = ${_true}


      AND ptu.permission_reference IS NOT NULL


      AND ptu.permission_reference IS NULL



    AND (UPPER(u.name) LIKE #{query.searchSql} ESCAPE '/')

mapper.select:
SELECT ptu.template_id as templateId, ptu.permission_reference as permission, count(u.login) as count FROM users u INNER JOIN perm_templates_users ptu ON ptu.user_id=u.id AND ptu.template_id in
#{id}
AND u.active = ${_true}
GROUP BY ptu.template_id, ptu.permission_reference

SELECT count(name) as count, permission, templateId FROM (SELECT g.name as name, ptg.permission_reference as permission, ptg.template_id as templateId FROM groups g INNER JOIN perm_templates_groups ptg ON ptg.group_id=g.id UNION -- Add Anyone group permission SELECT #{anyoneGroup} as name, ptg.permission_reference as permission, ptg.template_id as templateId FROM perm_templates_groups ptg
AND ptg.group_id IS NULL
) groups
AND groups.templateId in
#{id}
GROUP BY groups.permission, groups.templateId
