mapper.select:
select u.login from properties p inner join users u on p.user_id = u.id
left outer join projects p1 on p.resource_id=p1.id and p1.kee=#{componentKey}
where p.prop_key = #{propKey} and p.text_value like 'true' and ( p.resource_id is null
or p.resource_id=p1.id
)

select

from 
  properties p
where 
  p.prop_key=#{key}

    and p.resource_id is null


    and p.resource_id=#{resourceId}


    and p.user_id is null


    and p.user_id=#{userId}

select
from properties p where p.prop_key in
#{key}
and p.resource_id is null
and p.resource_id=#{componentId}
and p.user_id is null order by p.id

select
from properties p where p.prop_key in
#{key}
and p.resource_id in
#{componentId}
and p.user_id is null

select

from
  properties p


    and p.prop_key=#{query.key}


    and p.resource_id=#{query.componentId}


    and p.user_id=#{query.userId}

mapper.delete:
