mapper.update:
UPDATE groups SET
name=#{name,jdbcType=VARCHAR},
description=#{description,jdbcType=VARCHAR},
updated_at=#{updatedAt,jdbcType=TIMESTAMP}
WHERE id=#{id}

mapper.select:
select
  count(1)
from groups g
where
  g.organization_uuid = #{organizationUuid,jdbcType=VARCHAR}
  and g.id = #{groupId,jdbcType=BIGINT}

