mapper.select:
SELECT

FROM users u
WHERE u.login=#{login}

SELECT

FROM users u
WHERE
u.login=#{scmAccount}
OR u.email=#{scmAccount}
OR u.scm_accounts like #{likeScmAccount}

SELECT

FROM users u
WHERE u.id=#{id}

SELECT

FROM users u
WHERE u.login=#{id} AND u.active=${_true}

SELECT
FROM users u WHERE u.login in
#{login}

SELECT
FROM users u WHERE u.id in
#{id}

SELECT

FROM users u


    u.login IN

      #{login}



    AND u.active=${_true}


    AND (u.login LIKE #{searchTextSql} ESCAPE '/' OR u.name LIKE #{searchTextSql} ESCAPE '/')


    AND u.is_root = ${_true}


    AND u.is_root = ${_false}


ORDER BY u.name

SELECT count(1)
FROM users u
where lower(u.email)=#{email} AND u.active=${_true}

select
  count(1)
from
  users u
where
  u.active = ${_true}
  and u.is_root = ${_true}
  and u.login <> #{login}

mapper.delete:
DELETE FROM properties

  text_value LIKE #{login}
  AND prop_key IN

    #{property}

mapper.update:
update users set
  is_root = #{root,jdbcType=BOOLEAN},
  updated_at=#{now,jdbcType=BIGINT}
where
  login = #{login,jdbcType=VARCHAR}
  and active = ${_true}

update users u set
  is_root = ${_true},
  updated_at = #{now,jdbcType=BIGINT}
where
  u.id = #{userId,jdbcType=BIGINT}
  and exists (

    union

  )

update u set
  is_root = ${_true},
  updated_at = #{now,jdbcType=BIGINT}
from users u
where
  u.id = #{userId,jdbcType=BIGINT}
  and exists (

    union

